import React, { useState, useEffect } from 'react';

function Analyzer() {
  const [input, setInput] = useState('');
  const [response, setResponse] = useState(null);
  const [loading, setLoading] = useState(false);
  const [cluster, setCluster] = useState('');

  useEffect(() => {
    const savedCluster = localStorage.getItem('userCluster');
    if (savedCluster) setCluster(savedCluster);
  }, []);

  const handleAnalysis = async () => {
    if (!input.trim()) {
      setResponse({ output: '‚ùå ÿßŸÑÿ±ÿ¨ÿßÿ° ÿ•ÿØÿÆÿßŸÑ ŸÜÿµ ŸÑÿ™ÿ≠ŸÑŸäŸÑŸá', popups: [] });
      return;
    }

    setLoading(true);
    setResponse(null);

    try {
      const res = await fetch('https://heartbridge-unified3.onrender.com/api/predict', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ text: input, cluster })
      });

      const data = await res.json();
      setResponse(data || { output: '‚ö†Ô∏è ŸÑŸÖ Ÿäÿ™ŸÖ ÿßŸÑÿ≠ÿµŸàŸÑ ÿπŸÑŸâ ŸÜÿßÿ™ÿ¨.', popups: [] });
    } catch {
      setResponse({ output: '‚ùå ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿ®ÿßŸÑÿ≥Ÿäÿ±ŸÅÿ±.', popups: [] });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="app-container">
      <h2>ÿ™ÿ≠ŸÑŸäŸÑ ÿßŸÑÿπŸÑÿßŸÇÿ© - {cluster && `ŸÜŸÖÿ∑ŸÉ: ${cluster}`}</h2>

      <textarea
        className="input-box"
        value={input}
        onChange={(e) => setInput(e.target.value)}
        placeholder="ÿßŸÜÿ≥ÿÆ ŸÜÿµ ÿßŸÑÿ≠Ÿàÿßÿ± ŸáŸÜÿß..."
        rows={6}
      />

      <br />
      <button className="analyze-button" onClick={handleAnalysis} disabled={loading}>
        {loading ? '...ÿ¨ÿßÿ±Ÿç ÿßŸÑÿ™ÿ≠ŸÑŸäŸÑ' : 'ÿßÿ®ÿØÿ£ ÿßŸÑÿ™ÿ≠ŸÑŸäŸÑ'}
      </button>

      {response?.output && <div className="result-box"><pre className="output-text">{response.output}</pre></div>}

      {response?.popups?.length > 0 && (
        <div className="popup-box">
          <strong>üß† ŸÖŸÑÿßÿ≠ÿ∏ÿßÿ™ ÿ∞ŸÉŸäÿ©:</strong>
          <ul>
            {response.popups.map((popup, i) => <li key={i}>{popup}</li>)}
          </ul>
        </div>
      )}
    </div>
  );
}

export default Analyzer;
